mapper.select:
 SELECT
		COUNT(*) FROM CLAIMS c JOIN INSUREDS i ON i.INSURED_ID = c.INSURED_ID
		JOIN PERSONS p ON p.PERSON_ID = i.PERSON_ID 

				AND (INSTR(LOWER(p.PERSON_NAME), LOWER(#{c.q})) > 0
				OR INSTR(LOWER(c.CLAIM_ID), LOWER(#{c.q})) > 0)


				AND c.CLAIM_TYPE = #{c.claimType}


				AND c.CLAIM_STATUS = #{c.status}

			AND c.CLAIM_DATE >= #{c.from}
			AND c.CLAIM_DATE <= #{c.to}

 SELECT
		c.CLAIM_ID, p.PERSON_NAME, c.CLAIM_TYPE, c.ACCIDENT_DATE, c.CLAIM_DATE,
		c.CLAIM_STATUS FROM CLAIMS c LEFT JOIN CUSTOMERS cu ON c.CUSTOMER_ID =
		cu.CUSTOMER_ID LEFT JOIN PERSONS p ON cu.PERSON_ID = p.PERSON_ID 

				AND (LOWER(p.PERSON_NAME) LIKE '%' || LOWER(#{c.q}) || '%'
				OR TO_CHAR(c.CLAIM_ID) LIKE '%' || #{c.q} || '%')



				AND c.CLAIM_TYPE = #{c.claimType}



				AND c.CLAIM_STATUS = #{c.status}




				AND c.CLAIM_DATE >= #{c.from, jdbcType=DATE}


				AND c.CLAIM_DATE < #{c.toExclusive, jdbcType=DATE}

		 ORDER BY
		c.CLAIM_ID DESC
