mapper.select:
SELECT * FROM (
    SELECT a.*, ROWNUM rnum
    FROM (
        SELECT faq_id, category, question, answer, created_at, updated_at
        FROM faq
        ORDER BY faq_id
    ) a
    WHERE ROWNUM <= #{endRow,jdbcType=INTEGER}
)
WHERE rnum >= #{startRow,jdbcType=INTEGER}

SELECT * FROM (
    SELECT a.*, ROWNUM rnum
    FROM (
        SELECT faq_id, category, question, answer, created_at, updated_at
        FROM faq
        WHERE category = #{category,jdbcType=VARCHAR}
        ORDER BY faq_id
    ) a
    WHERE ROWNUM <= #{endRow,jdbcType=INTEGER}
)
WHERE rnum >= #{startRow,jdbcType=INTEGER}

SELECT * FROM (
    SELECT a.*, ROWNUM rnum
    FROM (
        SELECT faq_id, category, question, answer, created_at, updated_at
        FROM faq
        WHERE LOWER(question) LIKE '%' || LOWER(#{keyword,jdbcType=VARCHAR}) || '%'
           OR LOWER(answer) LIKE '%' || LOWER(#{keyword,jdbcType=VARCHAR}) || '%'
        ORDER BY faq_id
    ) a
    WHERE ROWNUM <= #{endRow,jdbcType=INTEGER}
)
WHERE rnum >= #{startRow,jdbcType=INTEGER}
